//Timestamp Converter

function dateConvert(date) {
  const months = [
    "01",
    "02",
    "03",
    "04",
    "05",
    "06",
    "07",
    "08",
    "09",
    "10",
    "11",
    "12",
  ];
  const dateConvert = `${
    months[date.getMonth()]
  }/ ${date.getDate()}/ ${date.getFullYear()}`;

  return dateConvert;
}

export default dateConvert;
