import React, { Component } from "react";
import Header from "./components/Header";
import Video from "./components/Video";
import Description from "./components/Description";
import Comments from "./components/Comments";
import CommentsList from "./components/CommentsList";
import VideoList from "./components/VideosList";

// import uuid from "uuid/v1";
import axios from "axios";

export default class App extends Component {
  //axios calls

  state = {
    mainVideoDetails: {},
    sideVideos: [],
  };

  // Set URL and key in a variable?

  componentDidMount() {
    axios
      .get(
        "https://project-2-api.herokuapp.com/videos/1af0jruup5gu?api_key=87406025-19a3-40c6-88d5-0f1aeb285f89"
      )
      .then((response) =>
        this.setState({
          mainVideoDetails: response.data,
        })
      )
      .catch((err) => console.log(err));
    axios
      .get(
        "https://project-2-api.herokuapp.com/videos?api_key=87406025-19a3-40c6-88d5-0f1aeb285f89"
      )
      .then((response) =>
        this.setState({
          sideVideos: response.data,
        })
      )
      .catch((err) => console.log(err));
  }

  componentDidUpdate(prevProps) {
    console.log("comp updated");
    const oldPostId = prevProps.match.params.id;
    const newPostId = this.props.match.params.id;
    console.log("oldPostId", oldPostId);
    console.log("newPostId", newPostId);
    if (newPostId === oldPostId) {
      console.log("post id matches, no need to update state");
    } else {
      console.log("fetching new post");
      this.getPost(newPostId);
    }
  }

  /**
   * Get Single Post Data and update state
   */
  getPost(postId) {
    axios
      .get(
        "https://project-2-api.herokuapp.com/videos?api_key=87406025-19a3-40c6-88d5-0f1aeb285f89"
      )
      .then((response) => {
        this.setState({ post: response.data });
      })
      .catch((err) => console.log(err));
  }

  /**
   * Get Many Posts and update state
   */
  getPosts() {
    axios
      .get(
        "https://project-2-api.herokuapp.com/videos?api_key=87406025-19a3-40c6-88d5-0f1aeb285f89"
      )
      .then((response) => {
        // console.log(response.data);
        this.setState({ posts: response.data });
      })
      .catch((err) => console.log(err));
  }

  //wrap main video content with link component to main video id?

  render() {
    return (
      <>
        <Header />

        <Video mainVideo={this.state.mainVideoDetails} />
        <section className="wrapper-one">
          <section className="wrapper-two">
            <Description mainVideo={this.state.mainVideoDetails} />

            <Comments />
            <CommentsList mainVideo={this.state.mainVideoDetails} />
          </section>
          <VideoList
            sideVideo={this.state.sideVideos.filter(
              (video) => video.id !== this.state.mainVideoDetails.id
            )}
          />
        </section>
      </>
    );
  }
}
