function dateConvert(date) {
  const daysOfWeek = ["Sun", "Mon", "Tues", "Wed", "Thur", "Fri", "Sat"];
  const months = [
    "Jan",
    "Feb",
    "Mar",
    "Apr",
    "May",
    "Jun",
    "Jul",
    "Aug",
    "Sep",
    "Oct",
    "Nov",
    "Dec",
  ];

  const dateConvert = `${daysOfWeek[date.getDay()]}, ${
    months[date.getMonth()]
  }, ${date.getFullYear()}`;

  return dateConvert;
}

export default dateConvert;

